pragma solidity ^0.5.0;

contract Land {
    string public name;
    uint256 public plotCount = 0;
    mapping(uint256 => Plot) public plots;

    struct Plot {
        uint256 id;
        string name;
        uint256 price;
        address payable owner;
        bool purchased;
    }

    event PlotCreated(
        uint256 id,
        string name,
        uint256 price,
        address payable owner,
        bool purchased
    );

    event PlotPurchased(
        uint256 id,
        string name,
        uint256 price,
        address payable owner,
        bool purchased
    );

    constructor() public {
        name = "Etherverse";
    }

    function createPlot(string memory _name, uint256 _price) public {
        // Require a valid name
        require(bytes(_name).length > 0);
        // Require a valid price
        require(_price > 0);
        // Increment product count
        plotCount++;
        // Create the product
        plots[plotCount] = Plot(plotCount, _name, _price, msg.sender, false);
        // Trigger an event
        emit PlotCreated(plotCount, _name, _price, msg.sender, false);
    }

    function purchasePlot(uint256 _id) public payable {
        // Fetch the product
        Plot memory _plot = plots[_id];
        // Fetch the owner
        address payable _seller = _plot.owner;
        // Make sure the product has a valid id
        require(_plot.id > 0 && _plot.id <= plotCount);
        // Require that there is enough Ether in the transaction
        require(msg.value >= _plot.price);
        // Require that the product has not been purchased already
        require(!_plot.purchased);
        // Require that the buyer is not the seller
        require(_seller != msg.sender);
        // Transfer ownership to the buyer
        _plot.owner = msg.sender;
        // Mark as purchased
        _plot.purchased = true;
        // Update the product
        plots[_id] = _plot;
        // Pay the seller by sending them Ether
        address(_seller).transfer(msg.value);
        // Trigger an event
        emit PlotPurchased(
            plotCount,
            _plot.name,
            _plot.price,
            msg.sender,
            true
        );
    }
}
